import React, { useState } from "react";
import "../Styles/ProfilProfesor.css";
import DropDownProfesor from "../Partials/DropDownProfesor";

function ProfilProfesor({ nume, numarElevi, intervalAcceptare, onSubmit }) {
  const [showPopup, setShowPopup] = useState(false);

  function handleFormSubmit(e) {
    e.preventDefault(); // Previne refresh-ul paginii
    onSubmit(e); // Apelează funcția de salvare
    setShowPopup(true); // Afișează pop-up-ul
    setTimeout(() => {
      setShowPopup(false); // Ascunde pop-up-ul după 2 secunde
    }, 2000);
  }

  return (
    <>
      <DropDownProfesor />
      <div className="containerProfil">
        <div className="profileIconContainer">
          <i className="bi bi-person-circle profileIcon"></i>
        </div>
        <h1>{nume}</h1>
        <div className="profesorDetails">
          <p>
            <strong>Număr de elevi:</strong> {numarElevi}{" "}
            <span>(maxim acceptat: 10 elevi)</span>
          </p>
          <p>
            <strong>Interval acceptare lucrări:</strong> {intervalAcceptare}
          </p>
        </div>
        <form onSubmit={handleFormSubmit} className="formContainer">
          <button type="submit" className="formButton">
            Salvează Modificările
          </button>
        </form>
      </div>
      {showPopup && (
        <div className="popupMessage">
          <p>Datele au fost salvate cu succes!</p>
          <i className="bi bi-check-circle-fill text-success"></i>
        </div>
      )}
    </>
  );
}

export default ProfilProfesor;
