import { useState } from "react";

const messages = [
  "Completare profil",
  "Solicita colaborare",
  "Raspuns cerere",
  "Trimite cerere",
  "Descarca cererea",
];

function StatusComponent() {
  const [step, setStep] = useState(0);

  function handleNext() {
    if (step < 4) {
      setStep((s) => s + 1);
    }
  }
  function handlePrev() {
    if (step > 0) {
      setStep((s) => s - 1);
    }
  }

  return (
    <div className="statusContainer">
      <div className={step >= 0 ? "active" : ""}>"Completare profil"</div>
      <div className={step >= 1 ? "active" : ""}>"Solicita colaborare"</div>
      <div className={step >= 2 ? "active" : ""}>"Raspuns cerere"</div>
      <div className={step >= 3 ? "active" : ""}>"Trimite cerere"</div>
      <div className={step >= 4 ? "active" : ""}>"Descarca cerere"</div>
      <button onClick={handleNext}>Next</button>
      <button onClick={handlePrev}>Previous</button>
    </div>
  );
}

export default StatusComponent;
