import { useState } from "react";

function StatusComponent({ stepProp, setStepProp }) {
  function handleNext() {
    if (step < 4) {
      setStep((s) => s + 1);
    }
  }
  function handlePrev() {
    if (step > 0) {
      setStep((s) => s - 1);
    }
  }

  return (
    <div className="statusContainer">
      <div className={step >= 0 ? "statusActive" : ""}>Completare profil</div>
      <div className={step >= 1 ? "statusActive" : ""}>Solicita colaborare</div>
      <div className={step >= 2 ? "statusActive" : ""}>Raspuns cerere</div>
      <div className={step >= 3 ? "statusActive" : ""}>Trimite cerere</div>
      <div className={step >= 4 ? "statusActive" : ""}>Descarca cerere</div>
      <div className="butoane">
        <button onClick={handleNext}>Next</button>
        <button onClick={handlePrev}>Previous</button>
      </div>
    </div>
  );
}

export default StatusComponent;
