import React, { useState } from "react";

function AcceptareColaborare() {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (selectedFile) {
      alert(`Fișierul ${selectedFile.name} a fost încărcat cu succes.`);
    } else {
      alert("Te rugăm să selectezi un fișier înainte de a trimite.");
    }
  };

  return (
    <div className="profesorContainer acceptareColaborare">
      <div className="iconContainer">
        <i className="bi bi-check-circle-fill text-accent"></i>
        <p className="text-accent fw-bold mt-2">Succes</p>
      </div>
      <p className="acceptareText">
        Cererea ta de colaborare a fost acceptată de către profesor. Încarcă mai
        jos documentul pentru a-l trimite către cadrul didactic pentru
        semnătură.
      </p>
      <form onSubmit={handleSubmit} className="fileUploadForm">
        <div className="mb-3">
          <input
            type="file"
            className="form-control fileInput"
            onChange={handleFileChange}
            required
          />
        </div>
        <button type="submit" className="formButton">
          Trimite Fișier
        </button>
      </form>
    </div>
  );
}

export default AcceptareColaborare;
